import React, {Component} from 'react';

class nav extends Component{

    constructor(props){
        super(props);

        this.state = {
            news: [],
            tweets: []
        }
    }

    componentDidMount() {
        //fill your own api details here
        this.interval = setInterval(() => {
            fetch('https://newsapi.org/v2/top-headlines?country=us&apiKey=')
                .then(response => response.json())
                .then(response => {
                    this.setState({news: response.articles});
                });
            //fill your own api details here
            fetch('https://api.twitter.com/1.1/statuses/user_timeline.json?screen_name=VancityReynolds&count=5',{headers: new Headers(
                {'Authorization': 'OAuth oauth_consumer_key="",oauth_token="",oauth_signature_method="HMAC-SHA1",oauth_timestamp="",oauth_nonce="kYjzVBB8Y0ZFabxSWbWovY3uYSQ2pTgmZeNu2VS4cg",oauth_version="1.0",oauth_signature=""'})
            })
                .then(response => response.json())
                .then(response => {
                    this.setState({tweets: response});
                });

        }, 5000);

    }

    componentWillUnmount() {
        clearInterval(this.interval);
    }

    render(){
        return (
            <nav className="Nav">
                <h4 className="NavTitle">Top tweets</h4>
                <ul className="List">
                    {this.state.tweets.map(tweet => <li>{tweet.text}</li>)}
                </ul>

                <h4 className="NavTitle">Top Headlines</h4>
                <ul className="List">
                    {this.state.news.slice(0,5).map(newss => <li>{newss.title}</li>)} 
                </ul>
            </nav>
        )
    }
}

export default nav;
