import React, {Component} from 'react';

class nav extends Component{

    constructor(props){
        super(props);

        this.state = {
            news: [],
            tweets: []
        }
    }

    componentDidMount() {
        //api integration
        this.interval = setInterval(() => {
            fetch('https://newsapi.org/v2/top-headlines?sources=google-news&apiKey=e4fe551042de4046af9183e62fae8a01')
                .then(response => response.json())
                .then(response => {
                    this.setState({news: response.articles});
                });
        }, 5000);

    }

    componentWillUnmount() {
        clearInterval(this.interval);
    }

    render(){
        return (
          <nav className="Nav">
              <h4 className="NavTitle">Top tweets</h4>
              <ul className="List">
                  {this.state.tweets.map(tweet => <li>{tweet.text}</li>)}
              </ul>

                <h4 className="NavTitle">Top Headlines</h4>
                <ul className="List">
                    {this.state.news.slice(0,5).map(newss => <li>{newss.title}</li>)}
                </ul>
            </nav>
        )
    }
}

export default nav;
